import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {UiActions} from "./store/ui-slice";

function App() {

    const fbdbURL = "https://react-http-realtime-db-json-default-rtdb.firebaseio.com/cart.json";

    const dispatchFn = useDispatch();
    const showCart = useSelector(state => state.ui.cartIsVisible);
    const cart = useSelector(state => state.cart);
    const notification = useSelector(state => state.ui.showNotification())

    useEffect(()=>{
        const sendCartData = async () => {
            dispatchFn(UiActions.showNotification({
                status:"Pending",
                title:"Sending",
                message:"Sending Cart Data"
            }))
            const response = await fetch(fbdbURL,{
                method:'PUT',
                body: JSON.stringify(cart)
            });

            if(!response.ok){
                throw new Error('Sending cart data failed');
            }

            dispatchFn(UiActions.showNotification({
                status:"success",
                title:"Success!",
                message:"Cart Data Sent Successfully"
            }))
        }

        sendCartData().catch(e =>
            dispatchFn(UiActions.showNotification({
                status:"error",
                title:"Error!",
                message:e.message
            })));

    },[cart,dispatchFn]);


  return (
    <Layout>
        {showCart && <Cart/>}
      <Products />
    </Layout>
  );
}

export default App;
