import {createSlice} from '@reduxjs/toolkit'
import {useDispatch} from "react-redux";


const uiSlice = createSlice({
    name: 'uiSlice',
    initialState: {cartIsVisible: false,notification:null},
    reducers:{
        // toggle is a ui action!
        toggle(state){
            state.cartIsVisible = !state.cartIsVisible;
        },
        showNotification(state,action){
            state.notification = {
                statue:action.payload.status,
                title:action.payload.title,
                message:action.payload.message,
            };
        }
    }
})

const fbdbURL = "https://react-http-realtime-db-json-default-rtdb.firebaseio.com/cart.json";

export const sendCartData = (cart) => {

    return async (dispatchFn) => {
        dispatchFn(UiActions.showNotification({
            status: "Pending",
            title: "Sending",
            message: "Sending Cart Data"
        }))

        const sendCartRequest = async () => {
            const response = await fetch(fbdbURL, {
                method: 'PUT',
                body: JSON.stringify(cart)
            });

            if (!response.ok) {
                throw new Error('Sending cart data failed');
            }

            dispatchFn(UiActions.showNotification({
                status: "success",
                title: "Success!",
                message: "Cart Data Sent Successfully"
            }))
        }

        try {
            await sendCartRequest();
        } catch (e) {
            dispatchFn(UiActions.showNotification({
                status: "error",
                title: "Error!",
                message: e.message
            }));
        }

    }
}

export const UiActions = uiSlice.actions;
export default uiSlice;
